mapper.update:
update tbl_order 
    set orderStatus = #{orderStatus}
where ono = #{ono}

mapper.select:
select tod.ono, max(tod.memberID) as memberID, max(tod.orderDate) as orderDate, max(tod.orderStatus) as orderStatus, max(tp.productName) as productName, sum(top.quantity) as quantity, max(concat(tpi.uuid,'_',tpi.imgName)) as imgName from tbl_order tod left outer join tbl_order_product top on tod.ono = top.ono left outer join tbl_product tp on top.pno =tp.pno left outer join tbl_product_img tpi on tpi.pno = tp.pno where tpi.imgOrd = 0
tod.orderStatus like concat('%', #{keyword}, '%')
group by tod.ono order by tod.ono desc limit #{skip}, #{size}
